template<class T>
struct reflect_trait{};

#define REFLECT_BEGIN(Type) \
template<> \
struct reflect_trait<Type> \
{ \
    template<class Func> \
    static void for_each_members(Type& type, Func&& func) \
    { \

#define REFLECT_PER_MEMBER(x) \
    func(#x, type.x);

#define REFLECT_END() \
    } \
};


#define REFLECT(Type, ...) \
REFLECT_BEGIN(Type) \
REFLECT_PP_FOREACH(REFLECT_PER_MEMBER, __VA_ARGS__); \
REFLECT_END()